mapper.select:
select 

from R_FNC_FUNCTION
where FNC_APPL_ID = #{fncApplId,jdbcType=VARCHAR}
  and FNC_PARENT_FUNC_ID = #{fncParentFuncId,jdbcType=VARCHAR}
  and FNC_FUNC_ID = #{fncFuncId,jdbcType=VARCHAR}

mapper.delete:
delete from R_FNC_FUNCTION
where FNC_APPL_ID = #{fncApplId,jdbcType=VARCHAR}
  and FNC_PARENT_FUNC_ID = #{fncParentFuncId,jdbcType=VARCHAR}
  and FNC_FUNC_ID = #{fncFuncId,jdbcType=VARCHAR}
